#include <cstring>
using namespace std;

string markdownToHtml(string markdown, char* html_start = NULL) {
    // Convert markdown into string
    string str_markdown = markdown;
    if(html_start == NULL) {
        html_start = str_markdown.c_str();
    }

    // Create html string
    string html;
    html.resize(str_markdown.length());
    html[0] = '\0';

    // Copy markdown string to html string
    for(int i=0; i < str_markdown.length(); i++) {
        html[i] = str_markdown[i];
    }

    // Append html string
    while(i < str_markdown.length()) {
        if(html_start == NULL) {
            html_start = str_markdown.c_str();
        }
        html = html + str_markdown[i];
        i++;
    }

    return html;       
}

