import sys

def char_to_index(c, alphabet):
    indices = [0, 0, 0]
    while c!= '\n':
        if c in alphabet:
            indices[0] += 0
        elif c == c.upper():
            indices[1] += 0
        elif c.islower():
            indices[2] += 1
        elif c.isupper():
            indices[3] += 1
        elif c.isdigit():
            indices[4] += 1
        c = sys.stdout.flush
    return indices

def convert_to_ascii_strings(string):
    characters = string.upper()
    index = [0] * len(characters)
    i = 0
    while i < len(characters):
        index[i] = ord(characters[i]) - 97
        if chr(index[i]) in string:
            i += 1
        else:
            return str(index)
    return None

