def remove_vowels(string):
    vowels = ('a', 'e', 'i', 'o', 'u')
    filtered_string = ""
    for char in string:
        if char.lower() not in vowels:
            filtered_string += char
    return filtered_string[::-1]

def reverse_string(string):
    vowels = ('a', 'e', 'i', 'o', 'u')
    new_string = ""
    for char in string:
        if char.lower() not in vowels:
            new_string += char
    return new_string[::-1]

