def binary_search(array, element):
	left, right = 0, len(array) - 1
	while left <= right:
		mid = left + (right - left) // 2

		if array[mid] == element:
			return mid
		elif array[mid] < element:
			left = mid + 1
		else:
			right = mid - 1
	return -1
