def findMostEfficient(array): 
    result = 0
    current_max = -1
    max_value = -1
    for x in array: 
        result += x
        current_max = max(current_max, x) 
        if current_max == -1: 
            result += current_max * max_value 
            max_value, max_value_next = result, result 
            max_value_next = current_max + result 
        if current_max == max_value:
            max_value = current_max 
    return max_value

